        img = findViewById(R.id.imageView);
        btn.setOnClickListener(v -> {
            img.setBackgroundResource(R.drawable.a03);
            img.setImageResource(R.drawable.a04);
            Toast.makeText(this, '更换成功', Toast.LENGTH_SHORT).show();
